INSERT INTO `field` VALUES (57,'2014-08-28 00:45:14','2014-09-03 15:37:12','public_date','2012 - 2014',20079),(68,'2014-09-02 14:09:37','2014-09-04 11:56:56','product','\"[\"',20087),(73,'2014-09-03 12:50:02','2014-09-03 15:37:03','public_date','2012-2014',20089),(74,'2014-09-03 12:50:26','2014-09-03 15:36:45','public_date','2012-2014',20090),(75,'2014-09-03 13:06:38','2014-09-03 13:07:50','date','2012/06/12',20092),(76,'2014-09-03 13:07:25','2014-09-03 13:07:25','date','2013/05/21',20093),(78,'2014-09-03 13:43:55','2014-09-03 13:43:55','location','Shanghai',20094),(79,'2014-09-03 13:43:55','2014-09-03 13:43:55','report_to','Design Director',20094),(80,'2014-09-03 13:43:55','2014-09-03 13:43:55','general_role','<p>\n	<span style=\"color: rgb(158, 146, 122); font-family: Garamond, \'Microsoft Yahei\', simsun, arial, sans-serif; font-size: 18px; line-height: 24px;\">Assist all sales activities in store. Deliver quality and consistency of service including identifying customers&rsquo; needs, developing client relationships, sales, and delivering post-sale services.</span></p>\n',20094),(81,'2014-09-03 13:43:55','2014-09-03 13:43:55','key_responsibilities','<p>\n	<span style=\"color: rgb(158, 146, 122); font-family: Garamond, \'Microsoft Yahei\', simsun, arial, sans-serif; font-size: 18px; line-height: 24px;\">- To response to daily store operations&nbsp;</span><br style=\"color: rgb(158, 146, 122); font-family: Garamond, \'Microsoft Yahei\', simsun, arial, sans-serif; font-size: 18px; line-height: 24px;\" />\n	<span style=\"color: rgb(158, 146, 122); font-family: Garamond, \'Microsoft Yahei\', simsun, arial, sans-serif; font-size: 18px; line-height: 24px;\">- To assist in providing excellent customer service and maintain a pleasant shopping environment consistent with our brand image&nbsp;</span><br style=\"color: rgb(158, 146, 122); font-family: Garamond, \'Microsoft Yahei\', simsun, arial, sans-serif; font-size: 18px; line-height: 24px;\" />\n	<span style=\"color: rg